// a central store of data which any component can subscribe to by connecting
import axios from 'axios';
import { SET_CARD_LIST } from "../actions/types";

const intialState = {   // all initial values of redux state can be initiallized here
    CardItems : []
} 


const rootReducer = (state = intialState ,action) =>
{
    console.log(action);

    switch (action.type)
    {
        case SET_CARD_LIST: return {
                                ...state,
                                CardItems: action.payload,
                            };

        case 'GET_CARDS': {

            axios.get('https://jsonplaceholder.typicode.com/photos/')
                .then(res => {
                    console.log('list obtained');

                    return {
                        ...state,
                        CardItems: res.data.slice(0, 20)
                    };
                })
                .catch(err => {
                    console.log(err);
                    return { ...state }
                });
        }

        case 'DELETE_CARD': {

            axios.delete('https://jsonplaceholder.typicode.com/photos/' + action.payload)
            .then(res => {
                console.log('delete successful');

                return {
                    ...state,
                    CardItems: state.CardItems.filter(item => item.id !== action.payload)
                };
            })
            .catch(err => {
                console.log(err);
                return { ...state}
            });
        }

        default : return state
    }
}

export default rootReducer ;
