import React, { Component } from 'react';

/* Since Placeholder isOnline REST API for Testing and Prototyping
it doesnt change the data on the server but it appears like it*/

class Home extends Component
{

    state = {
        CardItems: []
    }

    componentDidMount() {
        axios.get('https://jsonplaceholder.typicode.com/photos/')
            .then(res => {
                console.log(res);
                this.setState({
                    CardItems: res.data.slice(0, 20)
                });
            })
    }
    render() {
        const { CardItems } = this.state
        const postList =
            CardItems.length ?
                (
                    CardItems.map(post => {
                        return (
                            <div className="card" key={post.id}>

                                <img class="avtar" src={post.thumbnailUrl} alt="Card Image" />
                                <div className="card-content">
                                    <Link to={'/PostList/' + post.albumId}>
                                        <span className="card-title red-text">{post.title}</span>
                                    </Link>
                                    <label className="title">{post.title}</label>
                                    <div>
                                        <label class="center" style={{'width':'30%'}}>
                                            <span class="metrics"><i class="fa fa-code-fork"> </i>Album &nbsp { post.albumId }</span>
                                        </label>

                                        <label class="center" style={{ 'width': '30%' }}>
                                        <a href="post.url"><span class="metrics" ><i class="fa fa-info-circle"></i>View Full Size </span></a> 
                                        </label>
                                    </div>

                                </div>
                            </div>
                        )
                    })
                ) :
                (   /* by the time post gets loaded show this */
                    <div className="center">
                        <img src="https://ui-ex.com/images/gif-transparent-loading-4.gif" style="width:150px" />
                    </div>
                );

        return (
            <div>
                <div className="container">
                    <h4 className="center">PostList</h4>
                    {postList}
                </div>
            </div>
        )
    }
}


export default Home;
