import React, { Component } from 'react';
import axios from 'axios';
import Looader from './Loader';
import Modal from './Modal';



/* Since Placeholder isOnline REST API for Testing and Prototyping
it doesnt change the data on the server but it appears like it*/

class Home extends Component
{
    constructor(props) {
        super(props);
        this.state = {
            CardItems: [],
            currentpostid : null
        };
        this.modelRef = React.createRef();
    }


    openInNewTab(link,e)
    {
        window.open(link, "new tab");
    }

    DeleteCard(id)
    {
        var l = document.getElementsByClassName('Loader');
        var cntnr = document.getElementsByClassName('container');
        l[0].style.display = 'block' ;  //ever API requeest should have loaders for better User Experience
        cntnr[1].style.opacity = '0.5';  //Little transition to show delete effect
        console.log(cntnr);


        axios.delete('https://jsonplaceholder.typicode.com/photos/'+id)
            .then(res => 
            {
                this.modelRef.current.click(); // demo use of ref 
                console.log('delete successful');
                l[0].style.display = 'none';
                cntnr[1].style.opacity = '1'; 

                this.setState(previousState => {
                    return {
                        CardItems: previousState.CardItems.filter(item => item.id !== id)
                    };
                });
            })
            .catch(err => {
                console.log(err);
            });
    }
    updateId = (id) =>
    {
        console.log(id);   
        this.setState({ currentpostid: id })
    }



    componentDidMount() {
        axios.get('https://jsonplaceholder.typicode.com/photos/')
            .then(res => {
                console.log(res);
                this.setState({
                    CardItems: res.data.slice(0, 20)
                });
            })

    }

    render() {
        const { CardItems } = this.state
        const CardList =
            CardItems.length ?
                (
                    CardItems.map(post => {
                        return (
                            <div className="col-md-4" key={post.id}>
                                <div className="mycard" >

                                    <div className="row">
                                        <div style={{ float: 'right' }} data-toggle="modal" data-target="#DeleteCard" onClick={this.updateId.bind(this,post.id)}>
                                            <em className="fa fa-trash delIcon" title="Delete"></em>                 
                                        </div>
                                    </div>

                                    <div className="row">
                                        <div className="center">
                                            <img className="avtar" src={post.thumbnailUrl} alt="Card Image" />
                                        </div>
                                    </div>

                                    <div className="row">
                                        <div className="title">
                                            <label className="title">{post.title}</label>
                                        </div>
                                    </div>

                                    <div className="row" id="metricrow">
                                        <label className="center width40" >
                                            <span className="metrics"><i className="fa fa-file-image-o"> </i>Album {'\u00A0'} {post.albumId}</span>
                                        </label>

                                        <label className="center width40">
                                            <span onClick={this.openInNewTab.bind(this, post.url)} ><span className="metrics" ><i className="fa fa-window-maximize"></i> {'\u00A0'}  View Full Size </span></span>
                                        </label>

                                    </div>
                                </div>
                            </div>

                        )
                    })
                ) :
                (   /* by the time post gets loaded show this */
                    <Looader/>
                );

        return (
            <div>
                <div className="container">
                    <h4 className="center">Card List</h4>
                    <div className="row">
                        {CardList}
                    </div>
                    <div className="Loader">
                        <Looader />
                    </div>
                    <div id="mybutton">
                        <i className="fa fa-plus-circle addCard" aria-hidden="true"></i>
                    </div>
                    <Modal>
                        <div>

                            <div className="modal fade" id="DeleteCard" role="dialog" aria-hidden="false" >
                                <div className="modal-dialog" role="document">
                                    <div className="modal-content">
                                        <div className="modal-header">
                                            <button type="button" ref={this.modelRef} className="close" data-dismiss="modal" aria-label="Close">
                                                <span aria-hidden="true">&times;</span>
                                            </button>
                                            <h4 className="modal-title" id="modalLabel">Modal Title</h4>
                                        </div>
                                        <div className="modal-body">
                                            Are you sure you want to delete this Card
                                        </div>
                                        <div className="modal-footer">
                                            <button type="button" className="btn btn-secondary" onClick={this.DeleteCard.bind(this, this.state.currentpostid)}>Yes</button>
                                            <button type="button" className="btn btn-secondary" data-dismiss="modal">Close</button>
                                        </div>
                                    </div>
                                </div>
                            </div>



                        </div>
                    </Modal>


                </div>
            </div>
        )
    }
}


export default Home;
