import React, { Component } from 'react';
import ReactDOM from 'react-dom';
const modalRoot = document.getElementById('modal-root');


class Modal extends Component {


    constructor(props) {
        super(props);
        this.el = document.createElement('div');
        console.log(modalRoot);
        this.modelChildRef = React.createRef();

    }

    componentDidMount() {
        // The portal element is inserted in the DOM tree after
        // the Modal's children are mounted, 
        modalRoot.appendChild(this.el);
    }

    componentWillUnmount() {
        modalRoot.removeChild(this.el);
    }


    render()
    {
        const { title, action, actionName } = this.props; //Destructuring props




        return ReactDOM.createPortal(
                <>
                {RefModal}
                </>,
                this.el,
            );

    }
}


const RefModal = React.forwardRef((props, ref) => //Forwarding reference of close button to parent
    (

    <div className="modal fade" id="DeleteCard" role="dialog" aria-hidden="true" >
        <div className="modal-dialog" role="document">
            <div className="modal-content">
                <div className="modal-header">
                    <button type="button" ref={ref} className="close" data-dismiss="modal" aria-label="Close">
                        <span aria-hidden="true">&times;</span>
                    </button>
                    <h4 className="modal-title" id="modalLabel"> {title}</h4>
                </div>

                <div className="modal-body">{this.props.children}</div>

                <div className="modal-footer row">
                    <div className="col-md-6 ">
                        <button type="button" className="btn btn-secondary right" data-dismiss="modal">Close</button>
                    </div>
                    <div className="col-md-6 ">
                        <button type="button" className="btn btn-primary left" onClick={action}>{actionName}</button>
                    </div>

                </div>
            </div>
        </div>
    </div>
));

export default Modal;
