import React, { Component } from 'react';
import axios from 'axios';
import Looader from './Loader';
import Modal from './Modal';
import { Link } from "react-router-dom";



/* Since Placeholder isOnline REST API for Testing and Prototyping
it doesnt change the data on the server but it appears like it*/

class Home extends Component
{
    constructor(props) {
        super(props);
        this.state = {
            CardItems: [],

            showLoader : false,
            order : 'none'
        };
    }

    componentDidMount()
    {

    }

    openTab = function (evt, tabName) {
        // Declare all variables
        var i, tabcontent, tablinks;

        // Get all elements with class="tabcontent" and hide them
        tabcontent = document.getElementsByClassName("tabcontent");
        for (i = 0; i < tabcontent.length; i++) {
            tabcontent[i].style.display = "none";
        }

        // Get all elements with class="tablinks" and remove the class "active"
        tablinks = document.getElementsByClassName("tablinks");
        for (i = 0; i < tablinks.length; i++) {
            tablinks[i].className = tablinks[i].className.replace(" active", "");
        }

        // Show the current tab, and add an "active" class to the button that opened the tab
        document.getElementById(tabName).style.display = "block";
        console.log(evt)
        evt.currentTarget.className += " active";
    }




    render()
    {

        return (
            <>
                <div className="container">
                    <div className="row">
                         <div className="col-md-12">
                            <h3 className="billheading">Airway Bill number</h3>
                            <h4 className="biillnumber">56538292</h4>

                        </div>
                    </div>

                    <div className="row">
                        <div className="col-md-12">
                            <div class="tab">
                                <button class="tablinks" id="defaultOpen" onClick={this.openTab.bind(this, 'Company_Profile')}><h4>Company Details</h4></button>
                                <button class="tablinks" onClick={this.openTab.bind(this, 'Company_Profile')}><h4>Bank Details</h4></button>
                                <button class="tablinks" onClick={this.openTab.bind(this, 'Company_Profile')}><h4>PAN & GST Details</h4></button>
                            </div>

                            <div id="Company_Profile" class="tabcontent">
                            </div>
                            <div id="Bank_Details" class="tabcontent">
                                <div ng-include="'app/views/company/bankdetails.html'"></div>
                            </div>
                            <div id="Bank_Details" class="tabcontent">
                                <div ng-include="'app/views/company/bankdetails.html'"></div>
                            </div>

                        </div>
                    </div>


                </div>
            </> /* Fragments prevent creatio of extra nodes */
        )
    }
}


export default Home;
