// a central store of data which any component can subscribe to by connecting

import axios from 'axios';
import { connect } from 'react-redux'


const intialState = {   // all initial values of redux state can be initiallized here

    CardItems : []
} 

axios.get('https://jsonplaceholder.typicode.com/photos/')
    .then(res => {
        console.log(res);

        //intialState.CardItems = res.data.slice(0, 20);
        //intialState.CardItemsCopy =  res.data.slice(0, 20);

        dispatch({ type: 'GET_CARDS', DATA: res.data.slice(0, 20) })

    })
    .catch((error) => {
        console.log(error);
    })

const rootReducer = (state = intialState ,action) =>
{
    switch (action.type)
    {
        case 'GET_CARDS':   return { ...state, CardItems: action.DATA };
    }
    return state;
}

export default rootReducer ;
