import React, { Component } from 'react';
import axios from 'axios'
import { Link } from 'react-router-dom'

/* Since Placeholder isOnline REST API for Testing and Prototyping
it doesnt change the data on the server but it appears like it*/

class Home extends Component
{

    state = {
        CardItems: []
    }

    componentDidMount() {
        axios.get('https://jsonplaceholder.typicode.com/photos/')
            .then(res => {
                console.log(res);
                this.setState({
                    CardItems: res.data.slice(0, 20)
                });
            })
    }
    render() {
        const { CardItems } = this.state
        const CardList =
            CardItems.length ?
                (
                    CardItems.map(post => {
                        return (
                            <div className="card col-md-4" key={post.id}>

                                <div class="row">
                                    <div class="center">
                                        <img className="avtar" src={post.thumbnailUrl} alt="Card Image" />
					                </div>
                                </div>

                                <div class="row">
                                    <div class="title">
                                        <label className="title">{post.title}</label>
                                    </div>

                                </div>


                                <div className="card-content">

                                    <div>
                                        <label className="center width40" >
                                            <span className="metrics"><i className="fa fa-code-fork"> </i>Album &nbsp { post.albumId }</span>
                                        </label>

                                        <label className="center width40">
                                        <a href="post.url"><span className="metrics" ><i className="fa fa-info-circle"></i>View Full Size </span></a> 
                                        </label>
                                    </div>

                                </div>
                            </div>
                        )
                    })
                ) :
                (   /* by the time post gets loaded show this */
                    <div className="center">
                        <img src="https://ui-ex.com/images/gif-transparent-loading-4.gif"  style={{width:'150px'}}/>
                    </div>
                );

        return (
            <div>
                <div className="container">
                    <h4 className="center">Card List</h4>
                    <div class="row">
                        {CardList}
                    </div>

                </div>
            </div>
        )
    }
}


export default Home;
