import React, { Component } from 'react';
import Looader from './Loader';
import ReactDOM from 'react-dom';
const modalRoot = document.getElementById('modal-root');


class Modal extends Component {


    constructor(props) {
        super(props);
        this.el = document.createElement('div');
        console.log(modalRoot);

    }

    componentDidMount() {
        // The portal element is inserted in the DOM tree after
        // the Modal's children are mounted, 
        modalRoot.appendChild(this.el);
    }

    componentWillUnmount() {
        modalRoot.removeChild(this.el);
    }


    render()
    {
        const { title, action } = this.props;

        const delModal = <div className="modal fade" id="DeleteCard" role="dialog" aria-hidden="true" >
                            <div className="modal-dialog" role="document">
                                <div className="modal-content">
                                    <div className="modal-header">
                                        <button type="button" ref={this.modelRef} className="close" data-dismiss="modal" aria-label="Close">
                                            <span aria-hidden="true">&times;</span>
                                        </button>
                                        <h4 className="modal-title" id="modalLabel"> Are you sure you want to delete this Card</h4>
                                    </div>

                                    <div className="modal-footer row">
                                        <div className="col-md-6 ">
                                            <button type="button" className="btn btn-secondary right" data-dismiss="modal">Close</button>
                                        </div>
                                        <div className="col-md-6 ">
                                            <button type="button" className="btn btn-primary left" onClick={this.DeleteCard.bind(this, this.state.currentpostid)}>Yes</button>
                                        </div>

                                    </div>
                                </div>
                            </div>
                        </div>




        return ReactDOM.createPortal(
                this.props.children,
                this.el,
            );

    }
}


export default Modal;
