// a central store of data which any component can subscribe to by connecting
import axios from 'axios';
import { SET_CARD_LIST } from "../actions/types";

const intialState = {   // all initial values of redux state can be initiallized here
    CardItems : []
} 


const rootReducer = (state = intialState ,action) =>
{
    console.log(action);

    if (action.type === SET_CARD_LIST)
    {
        return {
            ...state,
            CardItems: action.payload,
        };
    }
    else if (action.type === 'GET_CARDS')
    {

        return {
            ...state,
            CardItems: action.Cardlist.slice(0, 20)
        };

    }
    else if (action.type === 'DELETE_CARD') 
    {
        axios.delete('https://jsonplaceholder.typicode.com/photos/' + action.id)
            .then(res => {
                console.log('delete successful');

                return {
                    ...state,
                    CardItems: state.CardItems.filter(item => item.id !== action.id)
                };
            })
            .catch(err => {
                console.log(err);
                return { ...state }
            });
    }
    else
    {
        return state;
    }

}

export default rootReducer ;
