import React, { Component } from 'react';


/* Since Placeholder isOnline REST API for Testing and Prototyping
it doesnt change the data on the server but it appears like it*/

class Home extends Component
{
    constructor(props) {
        super(props);
        this.ref1 = React
        this.state = {
            CardItems: [],

            showLoader : false,
            order : 'none'
        };
    }

    componentDidMount()
    {

    }

    openTab(evt, tabName)
    {

        var i, tabcontent, tablinks;

        // Get all elements with className="tabcontent" and hide them
        tabcontent = $(".tabcontent");
        console.log(tabcontent);

        for (i = 0; i < tabcontent.length; i++) {
            tabcontent[i].css('display' ,"none");
        }

        // Get all elements with className="tablinks" and remove the class "active"
        tablinks = document.getElementsByClassName("tablinks");
        for (i = 0; i < tablinks.length; i++) {
            tablinks[i].className = tablinks[i].className.replace(" active", "");
        }

        // Show the current tab, and add an "active" class to the button that opened the tab
        document.getElementById(tabName).style.display = "block";
        console.log(evt)
        evt.currentTarget.className += " active";
    }




    render()
    {

        return (
            <>
                <div className="container">
                    <div className="row">
                         <div className="col-md-12">
                            <h3 className="billheading">Airway Bill number</h3>
                            <h4 className="biillnumber">56538292</h4>

                        </div>
                    </div>

                    <div className="row">
                        <div className="col-md-12">
                            <div className="tab">
                                <button className="tablinks" id="defaultOpen" onClick={this.openTab.bind(this, 'Shipment_status')}><h4>Shipment Status</h4></button>
                                <button className="tablinks" onClick={this.openTab.bind(this, 'Shipment_scan')}><h4>Shipment Scan</h4></button>
                                <button className="tablinks" onClick={this.openTab.bind(this, 'Shipment_items')}><h4>Shipment Items</h4></button>
                            </div>

                            <div id="Shipment_status" className="tabcontent">
                                Shipment_status
                            </div>
                            <div id="Shipment_scan" className="tabcontent">
                            Shipment_scan
                            </div>
                            <div id="Shipment_items" className="tabcontent">
                               Shipment_items
                            </div>

                        </div>
                    </div>


                </div>
            </> /* Fragments prevent creatio of extra nodes */
        )
    }
}


export default Home;
